/* eslint-disable consistent-return */
const request = require('supertest');
// eslint-disable-next-line no-unused-vars
const { ObjectId } = require('mongodb');
const { closeMongoDBConnection, connect } = require('../model/chatDB');

const app = require('../server'); // or wherever your server file is located

let mongo;

describe('POST report endpoint integration test', () => {
  /**
 * If you get an error with afterEach
 * inside .eslintrc.json in the
 * "env" key add -'jest': true-
*/
  let db; // eslint-disable-line
  /**
     * Make sure that the data is in the DB before running
     * any test
     * connect to the DB
     */
  beforeAll(async () => {
    mongo = await connect();
    db = mongo.db();
  }, 10000);

  /**
 * Delete all test data from the DB
 * Close all open connections
 */
  afterAll(async () => {
    try {
      // await db.collection('Reports').updateOne(
      //   { caption: 'qwerty' },
      // );
      await db.collection('Reports').deleteMany({ reporterid: 'testreporter' });
      await mongo.close();
      await closeMongoDBConnection(); // mongo client that started server.
    } catch (err) {
      return err;
    }
  });

  test('Post report 201', async () => {
    const resp = await request(app).post('/report').send({
      reporterid: 'testreporter',
      reporteeid: 'testreportee',
      caption: 'qwerty',
      date: new Date().getTime(),
      comments: [],
      likes: [],
      img: 'data:image/png;base64,',
    });
    expect(resp.status).toEqual(201);
  });
  test('Post report 400', async () => {
    const resp = await request(app).post('/report').send({
      reporterid: 'testreporter',
      reporteeid: 'testreportee',
      caption: 'qwerty',
      date: new Date().getTime(),
      comments: [],
      likes: [],
    });
    expect(resp.status).toEqual(400);
  });
});
